DROP TABLE IF EXISTS `tbl_appointment_order_detail`;
CREATE TABLE `tbl_appointment_order_detail` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT,
  `ORDER_NO` varchar(50) NOT NULL COMMENT '订单号',
  `MEM_CODE` varchar(50) NOT NULL COMMENT '会员CODE',
  `PATIENT_CODE` int(20) NOT NULL COMMENT '就诊人编号',
  `PATIENT_NAME` varchar(50) NOT NULL COMMENT '就诊人姓名',
  `DOCTOR_CODE` varchar(50) NOT NULL COMMENT '主治医生CODE',
  `DOCTOR_NAME` varchar(50) NOT NULL COMMENT '预约医生姓名',
  `DOCTOR_TITLE_NAME` varchar(50) NOT NULL COMMENT '预约医生职称',
  `DOCTOR_DEPT_CODE` varchar(50) NOT NULL COMMENT '预约医生科室CODE',
  `DOCTOR_DEPT_NAME` varchar(50) NOT NULL COMMENT '预约医生科室名称',
  `DOCTOR_HOSPITAL_CODE` varchar(50) NOT NULL COMMENT '预约医生所在code',
  `DOCTOR_HOSPITAL_NAME` varchar(50) NOT NULL COMMENT '预约医生所在医院',
  `ATTENDANCE_TIME` datetime NOT NULL COMMENT '就诊时间',
  `ATTENDANCE_HOSPITAL_CODE` varchar(50) NOT NULL COMMENT '就诊医院code',
  `ATTENDANCE_HOSPITAL_NAME` varchar(50) NOT NULL COMMENT '就诊医院名称',
  `SERVICE_STATUS` varchar(10) NOT NULL COMMENT '服务状态',
  `REMARK` varchar(1000) DEFAULT NULL COMMENT '备注',
  `UPDATE_TIME` datetime NOT NULL COMMENT '更新时间',
  `CREATE_TIME` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`ID`),
  UNIQUE KEY `UNIQ_ORDER_NO` (`ORDER_NO`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=167 DEFAULT CHARSET=utf8mb4 COMMENT='预约订单详情表';

