DROP TRIGGER IF EXISTS `yuyue`;
DELIMITER ;;
CREATE TRIGGER `yuyue` AFTER INSERT ON `tbl_appointment_order_detail` FOR EACH ROW begin
	预约类型,
	医生姓名,
	订单号,
	患者姓名,
	患者性别,
	患者年龄,
	患者联系电话,
	患者所患疾病,
	患者病情描述,
	预约时间
  )
	SELECT 
	CASE o.ORDER_TYPE
		WHEN '05' THEN '远程门诊'
		WHEN '06' THEN '面诊预约'
		WHEN '07' THEN '手术预约'
		ELSE '其他' END
	AS '预约类型',
	AOD.DOCTOR_NAME AS '医生姓名', 
	AOD.ORDER_NO as '订单号', 
-- AOD.MEM_CODE AS '用户code', 
-- m.NICK_NAME AS '用户姓名', 
	AOD.PATIENT_NAME AS '患者姓名', 
	CASE PI.SEX
		WHEN '0' THEN '男'
		WHEN '1' THEN '女'
		ELSE '其他' END
	AS '患者性别',
PI.AGE AS '患者年龄', 
PI.PHONE AS '患者联系电话', 
PI.DISEASE AS '患者所患疾病',
REPLACE(REPLACE(PI.DISEASE_DESC, CHAR(10), ''), CHAR(13), '') AS '患者病情描述' ,
AOD.CREATE_TIME AS '预约时间'
FROM skk_srv_order_online.tbl_appointment_order_detail AOD 
LEFT JOIN skk_srv_order_online.tbl_patient_information PI ON PI.ORDER_NO = AOD.ORDER_NO
LEFT JOIN skk_srv_order_online.tbl_order o ON o.ORDER_NO = AOD.ORDER_NO 
LEFT JOIN skk_mem_member_online.tbl_member m ON m.CODE = AOD.MEM_CODE 
WHERE o.CREATE_TIME = new.CREATE_TIME
ORDER BY o.ORDER_TYPE,AOD.DOCTOR_NAME,AOD.CREATE_TIME;

end
;;
DELIMITER ;
